mapper.update:
UPDATE performance
performance_name=#{performanceName}, performance_title=#{performanceTitle}, performance_location=#{performanceLocation}, performance_date=#{performanceDate}, performance_content=#{performanceContent}, performance_image=#{performanceImage}, performance_user_id = #{performanceUserId}
WHERE performance_no=#{performanceNo}

mapper.select:
select , performance_hits
from(
	select rownum rn, , performance_hits
	from (
		select * 
		from performance
		where performance_code = 0
		order by performance_no desc
		)
	where rownum <= #{end}
)
where rn >= #{begin}

select , performance_hits
from(
	select rownum rn, , performance_hits
	from (
		select * 
		from performance
		where performance_code = 1
		order by performance_no desc
		)
	where rownum <= #{end}
)
where rn >= #{begin}

mapper.update:
update performance
set performance_hits = NVL(performance_hits, 0) + 1
where performance_no = #{performanceNo}

mapper.select:
select , performance_hits
from(
	select rownum rn, , performance_hits
	from (
		select * 
		from performance
		where performance_title like '%'||#{performanceTitle}||'%' AND performance_code = 0
		order by performance_no desc
		)
	where rownum <= #{end}
)
where rn >= #{begin}

select , performance_hits
from(
