select , performance_hits
from(
	select rownum rn, , performance_hits
	from (
		select * 
		from performance
		where performance_date >= #{sDate}
		and performance_date <= #{eDate}
		order by performance_no desc
		)
	where rownum <= #{end}
)
where rn >= #{begin}

select count(*) 
from performance
where performance_date >= #{sDate}
and performance_date <= #{eDate}
