mapper.select:
select 
from (
		select rownum, 
		from (
				select *
				from stage
				order by stage_no desc
		) where rownum < = #{end}
) where rownum >= #{begin}

select 
from (
	select rownum, 
	from (
			select *
			from stage
			where stage_location like '%'||#{stageLocation}||'%'
			order by stage_no desc
	) where rownum < = #{end}
) where rownum >= #{begin}

select 
from (
	select rownum, 
	from (
			select *
			from stage
			where stage_instrument like '%'||#{instrument}||'%'
			order by stage_no desc
	) where rownum < = #{end}
) where rownum >= #{begin}
