mapper.select:
select 
from (
		select rownum, 
		from (
				select *
				from stage
				order by stage_no desc
		) where rownum < = #{end}
) where rownum >= #{begin}

mapper.update:
UPDATE stage
STAGE_NAME = #{stageName}, STAGE_LOCATION = #{stageLocation}, STAGE_COST = #{stageCost}, STAGE_AREA = #{stageArea}, STAGE_INSTRUMENT = #{instrument}, STAGE_CONTENT = #{stageContent}, STAGE_PARKING = #{stageParking}, STAGE_DRINKING = #{stageDrinking}, STAGE_FOOD_SELL = #{stageFoodSell}, STAGE_FOOD_RESTRICTION = ${stageFoodRestriction}, STAGE_RESERVATION = #{stageReservation}, STAGE_SELLER_ID = #{stageSellerId}
WHERE STAGE_NO = #{stageNo}

mapper.select:
select count(*) 
from (	select *
			 		from stage
			 		where stage_rental_date >= #{startDate}  
			 		and stage_rental_date <= #{endDate}
			 		)
where stage_location like '%'||#{stageLocation}||'%'

select 
from (
	select rownum, 
	from (
			select *
			from (	select *
			 		from stage
			 		where stage_rental_date >= #{startDate}  
			 		and stage_rental_date <= #{endDate}
			 		)
			where stage_location like '%'||#{stageLocation}||'%'
			order by stage_no desc
	) where rownum < = #{end}
) where rownum >= #{begin}

select count(*) 
from (	select *
			 		from stage
			 		where stage_rental_date >= #{startDate}  
			 		and stage_rental_date <= #{endDate}
			 		)
where stage_instrument like '%'||#{stageInstrument}||'%'

select 
from (
	select rownum, 
	from (
			select *
			from (	select *
			 		from stage
			 		where stage_rental_date >= #{startDate}  
			 		and stage_rental_date <= #{endDate}
			 		)
			where stage_instrument like '%'||#{instrument}||'%'

			order by stage_no desc
	) where rownum < = #{end}
) where rownum >= #{begin}

select count(*) 
from stage
where stage_rental_date >= #{startDate}
and stage_rental_date <= #{endDate}

select 
	from (
		select rownum, 
		from (
				select *
				from stage
				where stage_rental_date >= #{startDate}
				and stage_rental_date <= #{endDate}
				order by stage_no desc
		) where rownum < = #{end}
	) where rownum >= #{begin}
