mapper.update:
UPDATE stage
STAGE_NAME = #{stageName}, STAGE_LOCATION = #{stageLocation}, STAGE_COST = #{stageCost}, STAGE_AREA = #{stageArea}, STAGE_INSTRUMENT = #{instrument}, STAGE_CONTENT = #{stageContent}, STAGE_PARKING = #{stageParking}, STAGE_DRINKING = #{stageDrinking}, STAGE_FOOD_SELL = #{stageFoodSell}, STAGE_FOOD_RESTRICTION = ${stageFoodRestriction}, STAGE_RESERVATION = #{stageReservation}, STAGE_SELLER_ID = #{stageSellerId}
