mapper.select:
select 
from (
		select rownum, 
		from (
				select *
				from stage
				order by stage_no desc
		) where rownum < = #{end}
) where rownum >= #{begin}

select *
from stage
where stage_no=#{stageNo}

mapper.update:
UPDATE stage
STAGE_NAME = #{stageName}, STAGE_LOCATION = #{stageLocation}, STAGE_COST = #{stageCost}, STAGE_AREA = #{stageArea}, STAGE_INSTRUMENT = #{stageInstrument}, STAGE_CONTENT = #{stageContent}, STAGE_PARKING = #{stageParking}, STAGE_DRINKING = #{stageDrinking}, STAGE_FOOD_SELL = #{stageFoodSell}, STAGE_FOOD_RESTRICTION = ${stageFoodRestriction}, STAGE_RESERVATION = #{stageReservation}, STAGE_RENTAL_DATE = #{stageRentalDate}, STAGE_START_TIME = #{stageStartTime}, STAGE_END_TIME = #{stageEndTime}, STAGE_SELLER_ID = #{stageSellerId}
WHERE STAGE_NO = #{stageNo}

mapper.select:
select count(*) 
from (	select 
			 		from stage
			 		where stage_rental_date >= #{startDate}  
			 		and stage_rental_date <= #{endDate}
			 		)
where stage_location like '%'||#{stageLocation}||'%'

